import React from 'react'
import { Link, NavLink } from 'react-router-dom';
import './NavigationBar.css'
import profileImg from '../images/icons/profile.svg';
import scheduleImg from '../images/icons/schedule.svg';
import { useNavigate } from "react-router-dom";

import { useState } from 'react';

function NavigationBar() {

const navigate = useNavigate();
const [item, setItem] = useState(1)

const [navItems, setNavItems] = [
  {id: 1, name: 'Профиль', img: profileImg, to: '/profile', active: true},
  {id: 2, name: 'Расписание', img: scheduleImg, to: '/schedule', active: false}
]

function onClick(to, id){
  navigate(to);
}

const activeStyle={
  backgroundColor: "blue"
}

return (
<nav class="navbar">
      <ul>

        {
          navItems.map((navItem) => (
            <li class="nav-item" style={ navItem.active ? activeStyle : ''} onClick={() => onClick(navItem.to, navItem.id)}>
                <img class="profile_icon" src={navItem.img}></img>
            </li>
          ))
        }


      </ul>
</nav>
);

}



export default NavigationBar;
