import React, {useState, useEffect} from 'react'
import Table from '../Table';
import Pagination from '../../components/Pagination';

function BillingPage() {

  const [countries, setCountries] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)
  const [countriesPerPage] = useState(10)


  useEffect(() => {
    const getCountries = async () => {
      setLoading(true)
      const res = await axios.get('https://restcountries.com/v3.1/all')
      setCountries(res.data)
      setLoading(false)
    }

    getCountries()
  }, [])

  const lastTableIndex =  currentPage * countriesPerPage
  const firstTableIndex = lastTableIndex - countriesPerPage
  const currentTableEntity = countries.slice(firstTableIndex, lastTableIndex)

  return (
    <>
  <div class="navbar navbar-expand-lg bg-body-tertiary">
    <div class="container-fluid position-relative">
      <h1>Биллинг</h1>
    </div>
  </div>

  <div className='container mt-5'>

    <Pagination countriesPerPage={countriesPerPage} totalTable={countries.length}/>

  </div>
  </>

  );
}

export default BillingPage;
