import React, {useState, useEffect} from 'react'
import Pagination from '../../components/Pagination';
import axios from 'axios';
import Billing from './tables/Billing';

function BillingPage() {

  const [countries, setCountries] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)
  const [countriesPerPage] = useState(10)

  const paginate = pageNumber => setCurrentPage(pageNumber)


  useEffect(() => {

    const getCountries = async () => {
      setLoading(true)
      const res = await axios.get('https://restcountries.com/v3.1/all')
      setCountries(res.data)
      console.log(res.data)
      setLoading(false)
    }

    getCountries()
  }, [])

  const lastTableIndex =  currentPage * countriesPerPage
  const firstTableIndex = lastTableIndex - countriesPerPage
  const currentTableEntity = countries.slice(firstTableIndex, lastTableIndex)

  const [searchValue, setSearchValue] = useState('')

  const filteredItems = countries.filter(country => {
    return country.name.common.toLowerCase().includes(searchValue.toLowerCase())
  })

  return (

  <>
  <div class="navbar navbar-expand-lg bg-body-tertiary">
    <div class="container-fluid position-relative">
      <h1>Биллинг</h1>
    </div>
  </div>

  <div className='container mt-5'>

    <div className='form'>
      <form className='search_form'>
        <input
            type='text'
            placeholder='Search...'
            className='search_input'
            onChange={(event) => setSearchValue(event.target.value)}
        />
      </form>
    </div>

    <Billing items={filteredItems} loading={loading}/>

    <Pagination tablePerPage={countriesPerPage} totalTable={filteredItems.length} paginate={paginate}/>

  </div>
  </>

  );
}

export default BillingPage;
