import React from 'react'
import { Link, NavLink } from 'react-router-dom';
import './NavigationBar.css'
import profileImg from '../images/icons/profile.svg';
import scheduleImg from '../images/icons/schedule.svg';
import { useNavigate } from "react-router-dom";

import { useState } from 'react';

function NavigationBar() {

const navigate = useNavigate();
const [item, setItem] = useState(1)

const [navItems, setNavItems] = [
  {id: 1, name: 'Профиль', img: '', to: '/profile', active: true},
  {id: 2, name: 'Расписание', img: '', to: '/schedule', active: false}
]

function onClick(to){
  navigate(to);
}

active

return (
<nav class="navbar">
      <ul>

        {
          navItems.map((navItem) => (
            <li class="nav-item" style={ navItem.active ? 'style = activeStyle' : ''}>
            <Link class="nav-link" to={navItem.to}>{navItem.name}</Link>
            </li>
          ))
        }

        <li class="nav-item" onClick={() => onClick('/profile')}>
            <img class="profile_icon" src={profileImg}></img>
        </li>

        <li class="nav-item" onClick={() => onClick('/schedule')}>
            <img class="profile_icon" src={scheduleImg}></img>
        </li>

      </ul>
</nav>
  );
}



export default NavigationBar;
